'use strict';
import React, {Component} from 'react';
import { View, Text, TextInput, StyleSheet, Image, PixelRatio, Alert, } from 'react-native';
import Button from "../components/Button";
import axios from 'axios';
import  qs  from "qs";
import Header from "../components/Header";
import { StackNavigator } from 'react-navigation';

class RegisterScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      password: '',
      cpasswod: '',
    };
  }
  render() {
    return (



      <View style={styles.view}>
      <Header title="REGISTER" />
        <View style={styles.editGroup}>
          <View style={styles.username}>
            <TextInput
              style={styles.edit}
              placeholder="Username"
              placeholderTextColor="#c4c4c4"
              onChangeText={(username) => this.setState({username})}
              />
          </View>
          <View style={{height: 1/PixelRatio.get(), backgroundColor:'#c4c4c4'}}/>
            <View style={styles.password}>
              <TextInput
              style={styles.edit}
              placeholder="Password"
              placeholderTextColor="#c4c4c4"
              secureTextEntry={true}
              onChangeText={(password) => this.setState({password})}
              />
            </View>
            <View style={{height: 1/PixelRatio.get(), backgroundColor:'#c4c4c4'}}/>
            <View style={styles.password}>
              <TextInput
              style={styles.edit}
              placeholder="Confirm Password"
              placeholderTextColor="#c4c4c4"
              secureTextEntry={true}
              onChangeText={(cpassword) => this.setState({cpassword})}
              />
            </View>
            <View style={{marginTop: 10}}>
              <Button text="Register" onPress={this._handleClick.bind(this)}/>
            </View>
        </View>
      </View>
    );
  }

  _handleClick() {

let config = {

  headers: {
         "Content-Type": "application/x-www-form-urlencoded;charset=UTF-8"
  }
};

let param = {
  "username": this.state.username,
  "password": this.state.password,
  "cpassword": this.state.cpassword
};


axios.post("http://localhost:5000/register", qs.stringify(param)).then(res => {
        // if(res.data === "error1"){
        //   Alert.alert("The passwords do not match. Please try again");
        // }
        // else if(res.data === "error2"){
        //   Alert.alert("The name already exists, please try again");
        // }
        // else if(res.data === "success"){
        //   navigate('Saved');
        // }
        switch (res.data) {
          case "error1":
            Alert.alert("The passwords do not match. Please try again");
            console.log(res.data);
            break;
          case "error2":
            Alert.alert("The name already exists, please try again");
            console.log(res.data);
            break;
          case "success":
            this.props.navigation.navigate('Saved')
            console.log(res.data);
            break;
        }

   }).catch(error => {
        console.log(error);
  })

  }
}


export default StackNavigator({
  Home: {
    screen: HomeScreen,
  },
  Saved: {
    screen: SavedScreen,
  },
}, {
    initialRouteName: 'Home',
});

const styles = StyleSheet.create({
  view: {
    flex: 1,
    backgroundColor: 'white',
  },
  editGroup: {
    margin: 20,
  },
  username: {
    marginTop: 100,
    height: 48,
    backgroundColor: 'white',
    justifyContent: 'center',
    borderTopLeftRadius: 3,
    borderTopRightRadius: 3,
  },
  password: {
    height: 48,
    backgroundColor: 'white',
    justifyContent: 'center',
    borderBottomLeftRadius: 3,
    borderBottomRightRadius: 3,
  },
  edit:{
    height: 40,
    fontSize: 13,
    backgroundColor: '#fff',
    paddingLeft: 15,
    paddingRight: 15,
  },
  h1: {
    color: "#389BF2",
    fontSize: 22,
