import { StyleSheet, Text, ScrollView, View, Image } from "react-native";
import { SearchBar } from 'react-native-elements';
import React, { useState, useEffect } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import Button from "../components/Button";
import DropDownItem from 'react-native-drop-down-item';

export default function SavedScreen({ navigation }) {


  var state = {
    contents: [
      {
        title: 'Title 1',
        body: 'Hi. I love this component. What do you think?',
      },
      {
        title: 'See this one too',
        body: 'Yes. You can have more items.',
      },
      {
        title: 'Thrid thing',
        body: 'What about very long text? What about very long text? What about very long text? What about very long text? What about very long text? What about very long text? What about very long text? What about very long text? What about very long text? What about very long text? What about very long text? What about very long text?',
      },
    ],
  };

  return (
    <View style={styles.container}>
      <SearchBar
        placeholder="Search"
        platform="ios"
      />
      <View>
        <Text style={styles.h1}>Your Lists</Text>
      </View>
      <View style={styles.container}>
        <ScrollView style={{ alignSelf: 'stretch' }}>
          {
            state.contents
              ? state.contents.map((param, i) => {
                return (
                  <DropDownItem
                    key={i}
                    style={styles.dropDownItem}
                    contentVisible={false}
                    header={
                      <View>
                        <Text style={{
                          fontSize: 16,
                          color: 'blue',
                        }}>{param.title}</Text>
                      </View>
                    }
                  >
                    <Text style={[
                      styles.txt,
                      {
                        fontSize: 20,
                      }
                    ]}>
                      {param.body}
                    </Text>
                  </DropDownItem>
                );
              })
              : null
          }
          <View style={{ height: 96 }} />
        </ScrollView>
      </View>
      <View style={{ marginTop: 10 }}>
        <Button text="Sign out" onPress={() => {
          try {
            AsyncStorage.removeItem(
              '@user_key'
            );
            navigation.navigate('Home');
          } catch (error) {
            // Error saving data
          }

        }} />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "white",
  },
  h1: {
    color: "#389BF2",
    fontSize: 22,
    marginTop: 22,
    marginLeft: 36,
  },
});
